CREATE TABLE %!%prefijo%!%botones (  uid int(11) NOT NULL auto_increment, idboton varchar(50) NOT NULL default '',  idimagencentro varchar(150) NOT NULL default '',  idimagenderecha varchar(150) NOT NULL default '',  idimagenizquierda varchar(150) NOT NULL default '',  idttf varchar(150) NOT NULL default '',  ttfsize int(11) NOT NULL default '0',  ancho int(11) NOT NULL default '0',  colortexto varchar(6) NOT NULL default '',  colortransparente varchar(6) NOT NULL default '',  colorfondo varchar(6) NOT NULL default '',correccionx int(11) NOT NULL default '0',  correcciony int(11) NOT NULL default '0',  descripcion text NOT NULL,  iduser int(11) NOT NULL default '0',  fecha datetime NOT NULL default '0000-00-00 00:00:00',  PRIMARY KEY  (uid),  UNIQUE KEY idboton (idboton)) 
CREATE TABLE %!%prefijo%!%config (  idconfig varchar(100) NOT NULL default '',  configvalue text NOT NULL,  iduser int(11) NOT NULL default '0',  date datetime NOT NULL default '0000-00-00 00:00:00',  PRIMARY KEY  (idconfig)) 
CREATE TABLE %!%prefijo%!%files (  idfile varchar(150) NOT NULL default '',  original_file varchar(250) NOT NULL default '',  category varchar(100) NOT NULL default '',  description text NOT NULL,  mimetype varchar(50) NOT NULL default '',  size bigint(11) NOT NULL default '0',  content longblob NOT NULL,  internal tinyint(1) NOT NULL default '0',  iduser int(11) NOT NULL default '0',  date datetime NOT NULL default '0000-00-00 00:00:00',  PRIMARY KEY  (idfile),  KEY categoria (category))
CREATE TABLE %!%prefijo%!%log (  idlog bigint(20) unsigned NOT NULL auto_increment,  date datetime NOT NULL default '0000-00-00 00:00:00',  iduser int(11) NOT NULL default '0',  ip varchar(15) NOT NULL default '',  idpagina varchar(100) NOT NULL default '',  content text NOT NULL,  PRIMARY KEY  (idlog))
CREATE TABLE %!%prefijo%!%objects (  idobject varchar(150) NOT NULL default '',  object longtext NOT NULL,  date datetime NOT NULL default '0000-00-00 00:00:00',  PRIMARY KEY  (idobject)) 
CREATE TABLE %!%prefijo%!%paginas (  idpagina varchar(50) NOT NULL default 'index',  tipo tinyint(4) NOT NULL default '0',titulo varchar(255) NOT NULL default '',texto longtext NOT NULL,  text_align varchar(20) NOT NULL default 'justify',enlace varchar(250) default NULL,img_mimetype varchar(50) NOT NULL default '',img_width smallint(255) unsigned NOT NULL default '0',img_height smallint(255) unsigned NOT NULL default '0',img_content longblob,  uid int(255) unsigned NOT NULL default '0',  uidroot int(11) NOT NULL default '0',uidparent int(255) unsigned NOT NULL default '0',fecha datetime NOT NULL default '0000-00-00 00:00:00',hits int(11) NOT NULL default '0',textohijas varchar(150) NOT NULL default 'Secciones',textolink varchar(150) NOT NULL default 'Enlace',num_noticias smallint(5) unsigned NOT NULL default '10',orden tinyint(3) unsigned NOT NULL default '1',activa tinyint(4) NOT NULL default '1',template varchar(100) NOT NULL default 'template_base.html',img_align varchar(15) NOT NULL default 'right',icono_content longblob NOT NULL,icono_align varchar(20) NOT NULL default 'right',icono_mimetype varchar(20) NOT NULL default '',descripcion text NOT NULL,variables text NOT NULL,idgroup tinyint(4) NOT NULL default '-1',iduser int(11) NOT NULL default '0',PRIMARY KEY  (uid),UNIQUE KEY idpagina (idpagina),KEY uid (uid),KEY uidroot (uidroot),KEY uidparent (uidparent),KEY fecha (fecha),KEY hits (hits),KEY activa (activa))
CREATE TABLE %!%prefijo%!%paginas_lang (idpagina varchar(50) NOT NULL default 'index',  lang char(2) NOT NULL default '',  tipo tinyint(4) NOT NULL default '0',  titulo varchar(255) NOT NULL default '',  texto longtext NOT NULL,  text_align varchar(20) NOT NULL default 'justify', enlace varchar(250) default NULL,  uid int(255) unsigned NOT NULL default '0',  uidroot int(11) NOT NULL default '0',  uidparent int(255) unsigned NOT NULL default '0',  fecha datetime NOT NULL default '0000-00-00 00:00:00',  hits int(11) NOT NULL default '0',  textohijas varchar(150) NOT NULL default 'Secciones',  textolink varchar(150) NOT NULL default 'Enlace',  num_noticias smallint(5) unsigned NOT NULL default '10',  template varchar(100) NOT NULL default 'template_base.html',  descripcion text NOT NULL, VARIABLES text NOT NULL, iduser int(11) NOT NULL default '0',  PRIMARY KEY  (uid,lang),  KEY uid (uid),  KEY uidroot (uidroot),  KEY uidparent (uidparent),  KEY fecha (fecha),  KEY hits (hits))
CREATE TABLE %!%prefijo%!%sessions (  idsession varchar(50) NOT NULL default '',  content longtext NOT NULL,  iddate datetime NOT NULL default '0000-00-00 00:00:00',  UNIQUE KEY idsession (idsession))
CREATE TABLE %!%prefijo%!%templates (  idtemplate varchar(50) NOT NULL default '',  content text NOT NULL,  content_header text NOT NULL,  content_footer text NOT NULL,  idtipo int(11) NOT NULL default '0',  lastmodified datetime NOT NULL default '0000-00-00 00:00:00',  iduser int(11) NOT NULL default '0',  descripcion text NOT NULL,  PRIMARY KEY  (idtemplate))
CREATE TABLE %!%prefijo%!%user_config (  uid int(11) NOT NULL auto_increment,  iduser int(11) NOT NULL default '0',  idconfig varchar(50) NOT NULL default '',  value text NOT NULL,  PRIMARY KEY  (uid))
CREATE TABLE %!%prefijo%!%users (  iduser int(10) unsigned NOT NULL auto_increment,  username varchar(50) NOT NULL default '',  password varchar(50) NOT NULL default '',  newpassword varchar(50) NOT NULL default '',  email varchar(150) default NULL,  idgroup int(11) NOT NULL default '0',  name varchar(200) NOT NULL default '',  notas text NOT NULL,  idadminpaginas tinyint(4) NOT NULL default '1',  PRIMARY KEY  (iduser),  UNIQUE KEY username (username))
CREATE TABLE %!%prefijo%!%_metadata ( uid bigint(20) unsigned NOT NULL default '0',  metadato varchar(50) NOT NULL default '',  valor longtext NOT NULL,  PRIMARY KEY  (uid,metadato)) COMMENT='Metadatos de las páginas';
